INSERT INTO `ship_types` (`id`, `nickname`, `picture`, `ru`, `rd`, `ra`, `rm`, `eps`, `cost`, `hull`, `panzerung`, `ablativeArmor`, `cargo`, `heat`, `crew`, `weapons`, `maxheat`, `torpedodef`, `shields`, `size`, `jdocks`, `adocks`, `sensorrange`, `hydro`, `recost`, `descrip`, `deutfactor`, `class`, `flags`, `groupwrap`, `ow_werft`, `chance4Loot`, `modules`, `hide`, `srs`, `scanCost`, `pickingCost`, `werft`, `version`, `minCrew`, `lostInEmpChance`, `maxunitsize`, `unitspace`, `nahrungcargo`, `versorger`, `bounty`) VALUES (67, 'GVB Osiris', 'data/dynamicContent/5180665b-40b9-4ae7-8316-f4137c0825fe.png', 0, '4', 6, 24, 80, '8', 600, 0, 0, 750, 10, 3, 'Prometheus=2|raketen=2|torpedos=1', 'Prometheus=8|raketen=4|torpedos=5', 0, 600, 2, 0, 0, 1, 0, 1, 'Die GVB Osiris war der Primäre Bomber der PVN während des großen Krieges. Sie zeigte ihr Alter während des zweiten shivanischen Krieges, da die fortschrittlicheren Bakha und Sekhmet Bomber sie in den meisten Bombergeschwadern ersetzt hatten. \r\nOsiris Bomber, die sich noch im Dienst befinden, sind nachgerüstet worden, um sie mit den meisten der Flugkörper und Bomben kompatibel zu machen - sie stellt in den Lost Lands das vasudanische Pendant zur TB Samurai dar. \r\nWenn sie durch Jägereskorten geschützt wird, kann eine Rotte Osiris Bomber große Verwüstung an feindlichen Kreuzern und Korvetten verursachen.', 0, 17, 'jaeger abfangen', 10, null, 0, '1:jprimary:Prometheus;2:jprimary:Prometheus', 0, 1, 1, 2, 0, 10, 3, 0.75, 1, 0, 250, 0, 0);
