CREATE TABLE schiff_einstellungen ( `id` int(11) NOT NULL AUTO_INCREMENT, `version` int(10) unsigned NOT NULL DEFAULT '0', `destsystem` int(11) NOT NULL DEFAULT '0', `destx` int(11) NOT NULL DEFAULT '0', `desty` int(11) NOT NULL DEFAULT '0', `destcom` text NOT NULL, `bookmark` tinyint(1) unsigned NOT NULL DEFAULT '0', `autodeut` tinyint(3) unsigned NOT NULL DEFAULT '1', `startFighters` tinyint(3) unsigned NOT NULL DEFAULT '0', `isfeeding` tinyint(1) NOT NULL DEFAULT '0', `isallyfeeding` tinyint(1) NOT NULL DEFAULT '0', `showtradepost` tinyint(4) NOT NULL DEFAULT '0', PRIMARY KEY (`id`), KEY `bookmark` (`bookmark`), KEY `idx_feeding` (`isfeeding`), KEY `idx_allyfeeding` (`isallyfeeding`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 PACK_KEYS=0;
UPDATE ships s, ships_modules sm SET s.startFighters=0 WHERE s.modules=sm.id AND sm.jdocks=0; UPDATE ships s, ship_types st SET s.startFighters=0 WHERE type=st.id AND st.jdocks=0 AND s.modules IS NULL;
UPDATE ships s, ships_modules sm SET s.isfeeding=0 WHERE s.modules=sm.id AND sm.versorger=0; UPDATE ships s, ship_types st SET s.isfeeding=0 WHERE type=st.id AND st.versorger=0 AND s.modules IS NULL;
ALTER TABLE ships ADD COLUMN einstellungen_id INT(11) DEFAULT NULL, ADD CONSTRAINT ships_fk_schiff_einstellungen FOREIGN KEY (einstellungen_id) REFERENCES schiff_einstellungen(id); INSERT INTO schiff_einstellungen SELECT id, 0, destsystem, destx, desty, destcom, bookmark, autodeut, startFighters, isfeeding, isallyfeeding,showtradepost FROM ships WHERE destsystem != 0 OR destx != 0 OR desty != 0 OR destcom != '' OR bookmark != 0 OR autodeut != 1 OR startFighters != 0 OR isfeeding != 0 OR isallyfeeding != 0 OR showtradepost!=0; UPDATE ships SET einstellungen_id=id WHERE id IN (select id from schiff_einstellungen); ALTER TABLE ships DROP COLUMN destsystem, DROP COLUMN destx, DROP COLUMN desty, DROP COLUMN destcom, DROP COLUMN bookmark, DROP COLUMN autodeut, DROP COLUMN startFighters, DROP COLUMN isfeeding, DROP COLUMN isallyfeeding, DROP COLUMN showtradepost;

