UPDATE offiziere set stationiertAufSchiff_id=CAST(substr(dest,3) AS DECIMAL) WHERE dest like "s %" and exists(select id from ships where id=CAST(substr(dest,3) as DECIMAL));
UPDATE offiziere set stationiertAufBasis_id=CAST(substr(dest,3) AS DECIMAL) WHERE dest like "b %" and exists(select id from bases where id=CAST(substr(dest,3) as DECIMAL));
update offiziere set stationiertAufBasis_id=(select id from bases where owner=offiziere.userid limit 1) where stationiertAufBasis_id is null and stationiertAufSchiff_id is null;
ALTER TABLE offiziere DROP COLUMN dest;

