UPDATE cargo_entries_units SET schiff_id=destid WHERE type=2 AND exists(SELECT id FROM ships where id=cargo_entries_units.destid);
UPDATE cargo_entries_units SET basis_id=destid WHERE type=1 AND exists(SELECT id FROM bases where id=cargo_entries_units.destid);
DELETE FROM cargo_entries_units WHERE schiff_id IS NULL and basis_id IS NULL;
