alter table config_felsbrocken drop foreign key fk_config_felsbrocken_system;
alter table config_felsbrocken_systems drop primary key, add column id bigint not null auto_increment, add primary key (id);
alter table config_felsbrocken_systems change column system system_id integer not null;
update config_felsbrocken set system=(select id from config_felsbrocken_systems cfs where cfs.system_id=system);
alter table config_felsbrocken change system system_id bigint not null;
alter table config_felsbrocken add CONSTRAINT fk_config_felsbrocken_system FOREIGN KEY (system_id) REFERENCES config_felsbrocken_systems (id);
alter table config_felsbrocken_systems add index config_felsbrocken_systems_fk_system (system_id), add constraint config_felsbrocken_systems_fk_system foreign key (system_id) references systems (id);
alter table config_felsbrocken_systems add unique system_id (system_id);
alter table upgrade_maxvalues add unique type (type);

alter table config_felsbrocken_systems add column name varchar(255);
alter table config_felsbrocken_systems drop index system_id;
update config_felsbrocken_systems set name="Felsbrocken";

alter table inttutorial change column reqsheet benoetigteSeite_id integer, change reqbase reqBase boolean not null, change reqname reqName boolean not null, change reqship reqShip boolean not null;
update inttutorial set benoetigteSeite_id=null where benoetigteSeite_id=0;
alter table inttutorial add index inttutorial_fk_inttutorial (benoetigteSeite_id), add constraint inttutorial_fk_inttutorial foreign key (benoetigteSeite_id) references inttutorial (id);
update inttutorial set headimg=concat('data/interface/interactivetutorial/',headimg);
